generated.css:
body{
    background: #232323;
    margin: 0;
    font-family: "Avenir", "Montserrat";
}

.square{
    width: 30%;
    background-color: purple;
    float: left;
    padding-bottom: 30%;
    margin: 1.66%;
    border-radius: 25%;
    transition: background-color 0.6s;
    -webkit-transition: background-color 0.6s;
    -moz-transition: background-color 0.6s;
}

.super-hard{
    width: 22.5%;
    padding-bottom: 22.5%;
    margin: 1.25%;

}

.hard{
    width: 28.5%;
    padding-bottom: 28.5%;
    margin: 1.66%;
}

#container{
    max-width: 600px;
    margin: 20px auto;
}

h1{
    color: white;
    text-align: center;
    background-color: steelblue;
    margin: 0;
    font-weight: normal;
    text-transform: uppercase;
    line-height: 1.1;
    padding: 20px 0;
}

#stripe{
    background-color: white;
    height: 30px;
    text-align: center;
    color: steelblue;
}

.selected{
    background-color: steelblue;
    color: white;
}

#color-display{
    font-size: 200%;
}

button{
    border: none;
    background: none;
    text-transform: uppercase;
    height: 100%;
    color: steelblue;
    font-weight: 700;
    letter-spacing: 1px;
    font-size: inherit;
    transition: all 0.3s;
    outline: none;
    -webkit-transition:  all 0.3s;
    -moz-transition: all 0.3s;
    margin: 0;
}

button:hover{
    background-color: steelblue;
    color: white;
}


#message{
    text-transform: uppercase;
    display: inline-block;
    width: 20%;
    font-weight: 700;
    letter-spacing: 1px;
    transition: color 0.3s;
    -webkit-transition:  color 0.3s;
    -moz-transition: color 0.3s;
}
